    protected function setUp()
    {
        $this->object = new Module;

        $app = $this->getMock('Zend\Mvc\Application', array(), array(), '', false);
        $this->canonicalizer = $this->getMock(
            'WebinoCanonicalRedirect\Uri\Canonicalizer',
